import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { View, Text, Image, ScrollView, StyleSheet, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';

interface Game {
  name: string;
  description: string;
  developer: string;
  publisher: string;
  released_date: string;
  id: string;
  image: string;
}

interface ApiResponse {
  success: boolean;
  count: number;
  data: Game[];
}

const Games = () => {
  const [gamesData, setGamesData] = useState<Game[]>([]);
  const navigation = useNavigation();

  useEffect(() => {
    axios
      .get<ApiResponse>('https://zelda.fanapis.com/api/games')
      .then((response) => {
        setGamesData(response.data.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleCardPress = (id: string) => {
    navigation.navigate('SingleGame', { gameId: id });
  };

  return (
    <ScrollView contentContainerStyle={styles.scrollContentContainer}>
      {gamesData.map((game) => (
        <TouchableOpacity
          key={game.id}
          style={styles.cardContainer}
          onPress={() => handleCardPress(game.id)}
        >
          <Text style={styles.title}>{game.name}</Text>
          <Image source={{ uri: game.image }} style={styles.image} />
          <Text style={styles.developer}>Developer: {game.developer}</Text>
          <Text style={styles.publisher}>Publisher: {game.publisher}</Text>
          <Text style={styles.releaseDate}>Release Date: {game.released_date}</Text>
        </TouchableOpacity>
      ))}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  scrollContentContainer: {
    backgroundColor: '#edb525',
    flexGrow: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  cardContainer: {
    alignItems: 'center',
    backgroundColor: 'white',
    borderRadius: 10,
    padding: 20,
    marginTop: 20,
    marginBottom: 20,
    width: '80%',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'black',
    marginTop: 5,
    marginBottom: 10,
  },
  image: {
    width: 250,
    height: 250,
    marginBottom: 10,
    borderRadius: 5,
  },
  developer: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
  publisher: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
  releaseDate: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
});

export default Games;
