import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { View, Text, Image } from 'react-native';

interface Game {
  name: string;
  description: string;
  developer: string;
  publisher: string;
  released_date: string;
  id: string,
  image: string
}

interface ApiResponse {
  success: boolean;
  count: number;
  data: Game[];
}

const Games = () => {
    const [gamesData, setGamesData] = useState<Game[]>([]);

    useEffect(() => {
      axios.get<ApiResponse>('https://zelda.fanapis.com/api/games')
        .then(response => {
          setGamesData(response.data.data);
        })
        .catch(error => {
          console.log(error);
        });
    }, []);

    const images = [
      'https://images.nintendolife.com/1834cc564d112/cover_250x250.jpg',
      'https://images.nintendolife.com/677f68e03288d/cover_250x250.jpg',
      'https://images.nintendolife.com/82f557404ec71/cover_250x250.jpg',
      'https://images.nintendolife.com/fa15d6e944666/cover_250x250.jpg',
      'https://images.nintendolife.com/225a29540bf44/cover_250x250.jpg',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',
      '',

      // ... Aggiungi gli URL delle altre immagini fino a URL_IMMAGINE_20
    ];

    return (
      <View>
        {gamesData.map((game, index) => (
          <View key={game.id}>
            <Image source={{ uri: images[index] || 'URL_IMMAGINE_FALLBACK' }} style={{ width: 100, height: 100 }} />
            <Text>{game.name}</Text>
            <Text>{game.description}</Text>
            <Text>Developer: {game.developer}</Text>
            <Text>Publisher: {game.publisher}</Text>
            <Text>Release Date: {game.released_date}</Text>
          </View>
        ))}
      </View>
    );
  }

  export default Games;
