import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { View, Text, StyleSheet } from 'react-native';

interface Game {
  name: string;
  description: string;
  developer: string;
  publisher: string;
  released_date: string;
  id: string;
}

const SingleGame = ({ route }: { route: any }) => {
  const { id } = route.params;
  const [gameData, setGameData] = useState<Game | null>(null);

  useEffect(() => {
    axios
      .get<Game>(`https://zelda.fanapis.com/api/games/${id}`)
      .then((response) => {
        setGameData(response.data.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [id]);

  if (!gameData) {
    return null; // Mostra uno stato di caricamento o un messaggio di errore, se necessario
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{gameData.name}</Text>
      <Text style={styles.developer}>Developer: {gameData.developer}</Text>
      <Text style={styles.publisher}>Publisher: {gameData.publisher}</Text>
      <Text style={styles.releaseDate}>Release Date: {gameData.released_date}</Text>
      <Text style={styles.description}>{gameData.description}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#edb525',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'black',
    marginBottom: 10,
  },
  developer: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
  publisher: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
  releaseDate: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
  description: {
    fontSize: 16,
    color: 'black',
    marginTop: 10,
  },
});

export default SingleGame;
