import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { View, Text, Image, ScrollView } from 'react-native';
import styles from './GamesStyles';

interface Game {
    name: string;
    description: string;
    developer: string;
    publisher: string;
    released_date: string;
    id: string,
    image: string
}

interface ApiResponse {
    success: boolean;
    count: number;
    data: Game[];
}

const Games = () => {
    const [gamesData, setGamesData] = useState<Game[]>([]);

    useEffect(() => {
        axios.get<ApiResponse>('https://zelda.fanapis.com/api/games')
            .then(response => {
                setGamesData(response.data.data);
            })
            .catch(error => {
                console.log(error);
            });
    }, []);

    const images = [
        'https://images.nintendolife.com/1834cc564d112/cover_250x250.jpg',
        'https://images.nintendolife.com/4e4ac48fbf2e7/cover_250x250.jpg',
        'https://images.nintendolife.com/82f557404ec71/cover_250x250.jpg',
        'https://images.nintendolife.com/fa15d6e944666/cover_250x250.jpg',
        'https://images.nintendolife.com/225a29540bf44/cover_250x250.jpg',
        'https://images.nintendolife.com/6c2a75a4be6a2/cover_250x250.jpg',
        'https://images.nintendolife.com/677f68e03288d/cover_250x250.jpg',
        'https://images.nintendolife.com/90cc1cac556c5/cover_250x250.jpg',
        'https://images.nintendolife.com/dcf5a93e79073/cover_250x250.jpg',
        'https://images.nintendolife.com/10d482fd29555/cover_250x250.jpg',
        'https://images.nintendolife.com/831e519e6287e/cover_250x250.jpg',
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQmw07yzY-qQiye52Oa-FgSD_ep_CpQ38xjioC3qIjdgym9xM-ZpoNo36u7hAUQfXJn7YU&usqp=CAU',
        'https://m.media-amazon.com/images/I/81EIqT3ARAL.jpg',
        'https://upload.wikimedia.org/wikipedia/it/8/85/Four_Swords_Adventures.jpg',
        'https://m.media-amazon.com/images/I/81kp83IceiL.jpg',
        'https://images.nintendolife.com/15a20bc6c7642/cover_250x250.jpg',
        'https://m.media-amazon.com/images/I/81kp83IceiL.jpg',
        'https://m.media-amazon.com/images/M/MV5BYTRhZmI0MmUtYjc1ZC00ZDdmLWE0OGUtYWViZTZjOTE3OThhXkEyXkFqcGdeQXVyMTA0MTM5NjI2._V1_FMjpg_UX1000_.jpg',
        'https://images.nintendolife.com/1d39c0a52b506/cover_250x250.jpg',
        'https://images.nintendolife.com/733d7b5ded134/cover_250x250.jpg',
    ];

    return (
        <View style={styles.container}>
            <ScrollView contentContainerStyle={styles.container}>
                {gamesData.map((game, index) => (
                    <View key={game.id} style={styles.cardContainer}>
                        <Image source={{ uri: images[index] || '' }} style={styles.image} />
                        <Text style={styles.title}>{game.name}</Text>
                        <Text style={styles.developer}>Developer: {game.developer}</Text>
                        <Text style={styles.publisher}>Publisher: {game.publisher}</Text>
                        <Text style={styles.releaseDate}>Release Date: {game.released_date}</Text>
                    </View>
                ))}
            </ScrollView>
        </View>
    );
};

export default Games;
