import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { View, Text } from 'react-native';

interface Game {
    name: string;
    description: string;
    developer: string;
    publisher: string;
    released_date: string;
    id: string;
  }

  interface ApiResponse {
    success: boolean;
    count: number;
    data: Game[];
  }

const Games = () => {
  const [gamesData, setGamesData] = useState([]);

  useEffect(() => {
    axios.get('https://zelda.fanapis.com/api/games')
      .then(response => {
        setGamesData(response.data.data);
      })
      .catch(error => {
        console.log(error);
      });
  }, []);

  return (
    <View>
      {gamesData.map(game => (
        <View key={game.id}>
          <Text>{game.name}</Text>
          <Text>{game.description}</Text>
          <Text>Developer: {game.developer}</Text>
          <Text>Publisher: {game.publisher}</Text>
          <Text>Release Date: {game.released_date}</Text>
        </View>
      ))}
    </View>
  );
}

export default Games;
