import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { View, Text, Image, StyleSheet } from 'react-native';
import images from '../../data/images'

interface Game {
  name: string;
  description: string;
  developer: string;
  publisher: string;
  released_date: string;
  id: string;
  image: string;
}

interface ApiResponse {
  success: boolean;
  count: number;
  data: Game[];
}

interface SingleGameProps {
  route: {
    params: {
      id: string;
    };
  };
}

const SingleGame: React.FC<SingleGameProps> = ({ route }) => {
  const { id } = route.params;
  const [gameData, setGameData] = useState<Game | null>(null);

  useEffect(() => {
    console.log('ID:', id);
    axios
      .get<ApiResponse>(`https://zelda.fanapis.com/api/games/${id}`)
      .then((response) => {
        const game = response.data.data;
        setGameData(game);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [id]);

  return (
    <View style={styles.container}>
      {gameData ? (
        <>
          <Text style={styles.title}>{gameData.name}</Text>
          <Image source={{ uri: gameData.image }} style={styles.image} />
          <Text style={styles.developer}>Developer: {gameData.developer}</Text>
          <Text style={styles.publisher}>Publisher: {gameData.publisher}</Text>
          <Text style={styles.releaseDate}>Release Date: {gameData.released_date}</Text>
          <Text style={styles.description}>{gameData.description}</Text>
        </>
      ) : (
        <Text>Loading...</Text>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  image: {
    width: 250,
    height: 250,
    marginBottom: 10,
    borderRadius: 5,
  },
  developer: {
    fontSize: 16,
    marginBottom: 5,
  },
  publisher: {
    fontSize: 16,
    marginBottom: 5,
  },
  releaseDate: {
    fontSize: 16,
    marginBottom: 5,
  },
  description: {
    fontSize: 16,
  },
});

export default SingleGame;
