import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  cardContainer: {
    borderWidth: 1,
    borderColor: '#ccc',
    padding: 10,
    marginBottom: 10,
  },
  image: {
    width: 100,
    height: 100,
  },
  title: {
    fontWeight: 'bold',
    fontSize: 16,
    marginTop: 5,
    marginBottom: 5,
  },
  description: {
    marginBottom: 5,
  },
  developer: {
    fontStyle: 'italic',
    marginBottom: 5,
  },
  publisher: {
    marginBottom: 5,
  },
  releaseDate: {
    marginBottom: 10,
  },
});

export default styles;
