import React from 'react';
import { View, Text, Image, StyleSheet, TouchableOpacity } from 'react-native';

interface Game {
  name: string;
  description: string;
  developer: string;
  publisher: string;
  released_date: string;
  id: string;
  image: string;
}

interface SingleCardProps {
  game: Game;
  onPress: () => void;
}

const SingleGame: React.FC<SingleCardProps> = ({ game, onPress }) => {
  const { name, description, developer, publisher, released_date, image } = game;

  return (
    <TouchableOpacity onPress={onPress} style={styles.container}>
      <Text style={styles.title}>{name}</Text>
      <Image source={{ uri: image }} style={styles.image} />
      <Text style={styles.description}>{description}</Text>
      <Text style={styles.developer}>Developer: {developer}</Text>
      <Text style={styles.publisher}>Publisher: {publisher}</Text>
      <Text style={styles.releaseDate}>Release Date: {released_date}</Text>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
    borderRadius: 10,
    padding: 20,
    marginBottom: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'black',
    marginBottom: 10,
  },
  image: {
    width: 250,
    height: 250,
    marginBottom: 10,
    borderRadius: 5,
  },
  description: {
    fontSize: 16,
    color: 'black',
    marginBottom: 10,
    textAlign: 'center',
  },
  developer: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
  publisher: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
  releaseDate: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
});

export default SingleGame;
