import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { View, Text, Image, StyleSheet } from 'react-native';

interface Game {
  name: string;
  description: string;
  developer: string;
  publisher: string;
  released_date: string;
  id: string;
  image: string;
}

interface ApiResponse {
  success: boolean;
  count: number;
  data: Game[];
}

interface SingleGameProps {
  id: string;
}

const SingleGame: React.FC<SingleGameProps> = ({ id }) => {
  const [gameData, setGameData] = useState<Game | null>(null);

  useEffect(() => {
    axios
      .get<ApiResponse>(`https://zelda.fanapis.com/api/games/${id}`)
      .then((response) => {
        const game = response.data.data;
        setGameData(game);
      })
      .catch((error) => {
        console.log(error);
      });
  }, [id]);

  if (!gameData) {
    return (
      <View style={styles.loadingContainer}>
        <Text>Loading...</Text>
      </View>
    );
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{gameData.name}</Text>
      {gameData.image ? (
        <Image source={{ uri: gameData.image }} style={styles.image} />
      ) : null}
      <Text style={styles.description}>{gameData.description}</Text>
      <Text style={styles.developer}>Developer: {gameData.developer}</Text>
      <Text style={styles.publisher}>Publisher: {gameData.publisher}</Text>
      <Text style={styles.releaseDate}>Release Date: {gameData.released_date}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  loadingContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'black',
    marginTop: 5,
    marginBottom: 10,
  },
  image: {
    width: 250,
    height: 250,
    marginBottom: 10,
    borderRadius: 5,
  },
  description: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
  developer: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
  publisher: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
  releaseDate: {
    fontSize: 16,
    color: 'black',
    marginBottom: 5,
  },
});

export default SingleGame;
